//функция для получения названия странны из номера телефона если не находит возвращает null
function checkTelephone($telephone)
{
	$codesCountry=$GLOBALS["codesCountry"];
	$country = null;
	foreach ($codesCountry as $key => $codes)
	{
		foreach($codes as $code)
		{
			$temp=stripos($telephone, $code);
			if ($temp!==false)
			{
				return $key;
			}
		}
	}
	return $country;
}

